mapper.select:
SELECT * FROM (
	SELECT ROWNUM T,RD.* FROM(
		(SELECT R.MEMBER_ID,D.ID,M.NAME,D.TITLE,D.CREATE_DT FROM REFERRER R
				FULL JOIN DOCUMENT D
				ON (R.DOCUMENT_ID = D.ID)
				FULL JOIN MEMBER M
				ON (M.ID = D.MEMBER_ID)
				WHERE 
				AND R.MEMBER_ID = #{member_id}
				ORDER BY D.CREATE_DT DESC
				)RD
			)
		)
WHERE T BETWEEN #{startRow} AND #{lastRow}
