mapper.select:
SELECT s.* FROM SCHEDULE s


        OR MEMBER_ID = #{member_id}


        OR TASK_ID = #{task_id}


        OR PROJECT_ID = #{project_id}


        OR ID = #{id}

mapper.delete:
DELETE FROM SCHEDULE


        OR MEMBER_ID = #{member_id}


        OR TASK_ID = #{task_id}


        OR PROJECT_ID = #{project_id}


        OR ID = #{id}
